

/*** cnvyr.min.js ***/
$(document).on("change","#printpass-form",function(h){var i=h.target.name;var a=$("#userreg_no").val();var d=$("#userEmail").val();var j=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;var g=/^[0-9]+$/;var f="Email is Required";var c="Email is not valid ";var b="Reg No. is required";var k="Reg No. is not Valid";switch(i){case"useremail":if(d==""){$(".useremail").html(f)}else{if(!j.test(d)){$(".useremail").html(c)}else{$(".useremail").html("")}}break;case"regno":if(a==""){$(".regno").html(b)}else{if(!g.test(a)){$(".regno").html(k)}else{$(".regno").html("")}}break;default:break}});$(document).on("submit","#printpass-form",function(d){var a=$("#userreg_no").val();var f=$("#userEmail").val();var c="Email is Required";var b="Registration Number is required";if(f==""){$(".useremail").html(c)}if(a==""){$(".regno").html(b)}if($(".regno").html()!=""||$(".useremail").html()!=""){return false}});$(document).on("click",".senddelemail",function(c){var d=$("#userreg_no").val()||$("#regno_get").val();var b=$("#sentuseremail").val()||$("#email_get").val();var a=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;if(b==""){$("#sentuser").html("Please Enter the Email")}else{if(!a.test(b)){$("#sentuser").html("Please Enter Valid Email")}else{$("#sentuser").html("")}}if($("#sentuser").html()==""){$("#sendsuccess").html('<img id="success-img" src="../images/me-loading.gif">');$.ajax({type:"get",url:api_emailPrintpass,data:{eventsignupId:d,userEmail:b},success:function(e){$("#sendsuccess").html(e.response.messages);setTimeout(function(){$("#sendsuccess").html("")},20000)},error:function(e){$("#sendsuccess").html(e.responseJSON.response.messages);setTimeout(function(){$("#sendsuccess").html("")},20000)}})}else{return false}});